mapper.update:
UPDATE uc_user
SET user_name = #{user.userName,jdbcType=VARCHAR},
psw = #{user.password,jdbcType=VARCHAR},
gender = #{user.gender,jdbcType=CHAR}
WHERE ID = #{user.id,jdbcType=INTEGER}

UPDATE uc_user
SET status = '2'
WHERE ID = #{userId,jdbcType=INTEGER}

mapper.select:
SELECT ID, user_name, gender, status, psw, create_datetime, update_datetime
FROM uc_user
WHERE
ID = #{userId}
user_name = #{userName}
